{
  "name": "@sourceborn/agent-workflows",
  "version": "0.1.18",
  "description": "Agent workflow utilities",
  "type": "module",
  "main": "./dist/index.cjs",
  "module": "./dist/index.js",
  "types": "./dist/index.d.ts",
  "bin": {
    "agent-workflows": "./dist/cli/index.js"
  },
  "exports": {
    ".": {
      "import": "./dist/index.js",
      "require": "./dist/index.cjs",
      "types": "./dist/index.d.ts"
    }
  },
  "files": [
    "dist"
  ],
  "scripts": {
    "build": "tsdown && tsdown src/cli/index.ts --format esm --outDir dist/cli",
    "devv": "tsdown --watch",
    "gen-slash-command-types": "node dist/cli/index.js generate-slash-types --input .claude/commands --output .agent/types/slash-commands.ts",
    "lint": "eslint .",
    "lint:fix": "eslint . --fix",
    "check-types": "tsc --noEmit",
    "check": "pnpm lint && pnpm check-types && pnpm test",
    "prepublishOnly": "pnpm run build",
    "publish:spectora": "./scripts/publish-spectora.sh",
    "ship": "pnpm ship:prepare && pnpm publish && pnpm publish:spectora",
    "ship:prepare": "pnpm build && git add . && git commit -m 'chore: prepare release' || true && pnpm version patch && git push && git push --tags",
    "test": "vitest run",
    "test:watch": "vitest"
  },
  "keywords": [
    "agent",
    "workflow",
    "spectora"
  ],
  "author": "Sourceborn",
  "license": "MIT",
  "repository": {
    "type": "git",
    "url": "https://github.com/spectorasoftware/agent-workflows"
  },
  "publishConfig": {
    "access": "public"
  },
  "dependencies": {
    "@sourceborn/agent-cli-sdk": "workspace:*",
    "boxen": "^8.0.1",
    "chalk": "^5.3.0",
    "gray-matter": "^4.0.3",
    "simple-git": "^3.28.0",
    "zod": "^4.1.12"
  },
  "devDependencies": {
    "@eslint/js": "^9.37.0",
    "@types/node": "^24.8.1",
    "@vitest/ui": "^3.2.4",
    "eslint": "^9.37.0",
    "globals": "^16.4.0",
    "tsdown": "^0.15.7",
    "typescript": "^5.9.3",
    "typescript-eslint": "^8.46.1",
    "vitest": "^3.2.4"
  },
  "engines": {
    "node": ">=22"
  }
}

