import { describe, it, expect } from "vitest";

/**
 * Get the first two initials from a user's name
 * @param name - The user's full name
 * @returns Two uppercase initials (e.g., "JD" for "John Doe")
 */
function getInitials(name: string): string {
  const parts = name.trim().split(/\s+/);

  if (parts.length === 0) return "??";
  if (parts.length === 1) {
    // Single name - take first two characters
    return parts[0].slice(0, 2).toUpperCase();
  }

  // Multiple names - take first character of first two parts
  return (parts[0][0] + parts[1][0]).toUpperCase();
}

describe("getInitials", () => {
  it("should return first two initials for a full name", () => {
    expect(getInitials("John Doe")).toBe("JD");
    expect(getInitials("Jane Smith")).toBe("JS");
  });

  it("should return first two characters for a single name", () => {
    expect(getInitials("John")).toBe("JO");
    expect(getInitials("Jane")).toBe("JA");
  });

  it("should handle names with multiple spaces", () => {
    expect(getInitials("John Michael Doe")).toBe("JM");
  });

  it("should handle lowercase names", () => {
    expect(getInitials("john doe")).toBe("JD");
  });

  it("should handle extra whitespace", () => {
    expect(getInitials("  John   Doe  ")).toBe("JD");
  });

  it("should return ?? for empty string", () => {
    expect(getInitials("")).toBe("??");
    expect(getInitials("   ")).toBe("??");
  });

  it("should handle single character names", () => {
    expect(getInitials("J")).toBe("J");
  });
});
