{"output":"import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@repo/ui/hooks/use-toast";
import type { TeamWithStats } from "@/domain/teams/services/getTeamsWithStats";

interface UpdateTeamData {
  id: string;
  data: {
    name?: string;
    key?: string;
    description?: string;
    team_lead_id?: string | null;
    is_visible?: boolean;
  };
}

// ==================== PRIVATE HELPERS ====================

/**
 * Update a specific team in the cached teams list
 * @private
 */
function updateTeamInCache(
  teamsData: { data: TeamWithStats[] } | undefined,
  teamId: string,
  updateData: UpdateTeamData["data"]
): { data: TeamWithStats[] } | undefined {
  if (!teamsData?.data) return teamsData;

  const updatedTeams = teamsData.data.map((team) => {
    if (team.id !== teamId) return team;

    // Find the new team lead info if team_lead_id is being updated
    let newTeamLead = team.team_lead;
    if ("team_lead_id" in updateData) {
      if (updateData.team_lead_id === null) {
        newTeamLead = null;
      } else if (
        updateData.team_lead_id &&
        updateData.team_lead_id !== team.team_lead_id
      ) {
        // Find the member info from team_members array
        const leadMember = team.team_members.find(
          (member) => member.id === updateData.team_lead_id
        );
        if (leadMember) {
          newTeamLead = {
            id: leadMember.id,
            name: leadMember.name,
            email: leadMember.email,
            avatar_url: leadMember.avatar_url,
          };
        }
      }
    }

    return {
      ...team,
      ...updateData,
      team_lead: newTeamLead,
    };
  });

  return { data: updatedTeams };
}

export function useUpdateTeam() {
  const queryClient = useQueryClient();
  const toast = useToast();

  return useMutation({
    mutationFn: async ({ id, data }: UpdateTeamData) => {
      const response = await fetch(`/api/teams/${id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to update team");
      }

      return response.json();
    },
    onMutate: async ({ id, data }) => {
      const queryKey = ["teams", "with-stats"];

      // Cancel any outgoing refetches
      await queryClient.cancelQueries({ queryKey });

      // Snapshot the previous value
      const previousTeams = queryClient.getQueryData(queryKey);

      // Optimistically update the cache
      queryClient.setQueryData(
        queryKey,
        (oldData: { data: TeamWithStats[] } | undefined) => {
          return updateTeamInCache(oldData, id, data);
        }
      );

      // Return context object with the snapshotted value
      return { previousTeams };
    },
    onError: (error: Error, variables, context) => {
      // If the mutation fails, use the context returned from onMutate to roll back
      if (context?.previousTeams) {
        queryClient.setQueryData(
          ["teams", "with-stats"],
          context.previousTeams
        );
      }

      toast.error(error.message || "Failed to update team");
    },
    onSuccess: () => {
      toast.success("Team updated successfully");
    },
    onSettled: () => {
      // Always refetch after error or success to ensure consistency
      queryClient.invalidateQueries({
        queryKey: ["teams", "with-stats"],
      });
      queryClient.invalidateQueries({
        queryKey: ["teams"],
      });
      // Invalidate dashboard queries to refresh team metrics
      queryClient.invalidateQueries({
        queryKey: ["dashboard"],
      });
    },
  });
}
