{"output":"import { useQuery, UseQueryOptions } from "@tanstack/react-query";
import { fetcher } from "@repo/utils/fetcher";

export interface PaginatedResponse<T> {
  data: T[];
  pagination: {
    page: number;
    pageSize: number;
    total: number;
    totalPages: number;
  };
}

interface UseApiListOptions<T> {
  endpoint: string;
  queryKey: unknown[];
  page?: number;
  pageSize?: number;
  filters?: Record<string, unknown>;
  enabled?: boolean;
  queryOptions?: Omit<
    UseQueryOptions<PaginatedResponse<T>>,
    "queryKey" | "queryFn" | "enabled"
  >;
}

/**
 * Hook for fetching paginated lists with filters and automatic cache management.
 * Provides pagination, filtering, and loading states out of the box.
 *
 * @template T - The type of items in the list
 * @param options - Configuration options
 * @param options.endpoint - API endpoint to fetch from
 * @param options.queryKey - React Query cache key
 * @param options.page - Current page number (default: 1)
 * @param options.pageSize - Number of items per page (default: 100)
 * @param options.filters - Filter parameters to apply
 * @param options.enabled - Whether to enable the query (default: true)
 * @param options.queryOptions - Additional React Query options
 * @returns Query result with data, loading, and error states
 *
 * @example
 * const { data, isLoading, error } = useApiList({
 *   endpoint: "/api/products",
 *   queryKey: ["products", "list"],
 *   page: currentPage,
 *   pageSize: 20,
 *   filters: { category: "electronics", inStock: true },
 *   enabled: !!selectedCategory
 * });
 *
 * // Access results
 * data?.data.forEach(product => console.log(product));
 * console.log(`Page ${data?.pagination.page} of ${data?.pagination.totalPages}`);
 */
export function useApiList<T>({
  endpoint,
  queryKey,
  page = 1,
  pageSize = 100,
  filters = {},
  enabled = true,
  queryOptions = {},
}: UseApiListOptions<T>) {
  const queryParams = new URLSearchParams({
    page: page.toString(),
    pageSize: pageSize.toString(),
    ...Object.entries(filters).reduce(
      (acc, [key, value]) => {
        if (value !== undefined && value !== null) {
          acc[key] = String(value);
        }
        return acc;
      },
      {} as Record<string, string>,
    ),
  });

  return useQuery({
    queryKey: [
      ...(Array.isArray(queryKey) ? queryKey : [queryKey]),
      { page, pageSize, filters },
    ],
    queryFn: async () => {
      const result = await fetcher<PaginatedResponse<T>>(
        `${endpoint}?${queryParams}`,
      );

      if (!result.ok) {
        throw new Error(result.error);
      }

      return result.data;
    },
    enabled,
    ...queryOptions,
  });
}
