import type { AvatarComboboxOption } from "@repo/ui/components/combobox";

// ==================== TYPES ====================

interface Member {
  id: string;
  name: string;
  email: string;
  avatar_url: string | null;
}

interface TeamLeadComboboxProps {
  currentLeadId?: string | null;
  members: Member[];
  onSelect: (memberId: string | null) => void;
  disabled?: boolean;
}

// ==================== PUBLIC API ====================

/**
 * Team lead selection combobox with member avatars
 * @public
 */
export function TeamLeadCombobox({
  currentLeadId,
  members,
  onSelect,
  disabled = false,
}: TeamLeadComboboxProps) {
  const memberOptions: AvatarComboboxOption<string>[] = members.map((member) => ({
    value: member.id,
    label: member.name,
    data: {
      avatar: member.avatar_url || undefined,
      description: member.email,
    },
  }));

  const handleValueChange = (value: string | null) => {
    onSelect(value);
  };

  return (
    <AvatarCombobox
      options={memberOptions}
      value={currentLeadId}
      onValueChange={handleValueChange}
      placeholder="Select team lead..."
      searchPlaceholder="Search members..."
      emptyText="No members found"
      clearable
      disabled={disabled}
      filterFn={(option, search) => {
        const searchLower = search.toLowerCase();
        return (
          option.label.toLowerCase().includes(searchLower) ||
          (option.data?.description?.toLowerCase().includes(searchLower) ?? false)
        );
      }}
    />
  );
}","metadata":{"exit_code":0,"duration_seconds":0.0}}

