import { getSession } from "@/domain/auth/services/getSession";
import { getTeamsWithStats } from "@/domain/teams/services/getTeamsWithStats";
import { getAllTeams } from "@/domain/teams/services/crudTeams";
import { z } from "zod";
import { ok } from "@repo/result";
import type { Result } from "@repo/result";
import type { TeamWithStats } from "@/domain/teams/services/getTeamsWithStats";

const querySchema = z.object({
  include: z.enum(['stats', 'basic']).optional().default('basic'),
  page: z.coerce.number().default(1),
  pageSize: z.coerce.number().default(100),
});

export const GET = createHandler({
  auth: getSession,
  querySchema,
  handler: async ({ query, ctx }): Promise<Result<{ data: TeamWithStats[]; total: number; page: number; pageSize: number }, Error>> => {
    const { include, page, pageSize } = query;

    if (include === 'stats') {
      const result = await getTeamsWithStats({ ctx, data: undefined });

      if (!result.ok) {
        return result as Result<{ data: TeamWithStats[]; total: number; page: number; pageSize: number }, Error>;
      }

      const teams = result.data as TeamWithStats[];
      const total = teams.length;
      const startIndex = (page - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const paginatedTeams = teams.slice(startIndex, endIndex);

      return ok({
        data: paginatedTeams,
        total,
        page,
        pageSize,
      });
    }

    const result = await getAllTeams({ ctx });

    if (!result.ok) {
      return result;
    }

    const teams = result.data.data; // getAllTeams returns paginated data
    const total = result.data.pagination.total;

    return ok({
      data: teams as unknown as TeamWithStats[],
      total,
      page,
      pageSize,
    });
  },
});","metadata":{"exit_code":0,"duration_seconds":0.0}}

