import { useDashboardPrefetch } from "./hooks/useDashboardPrefetch";
import { useTeamLeads } from "./hooks/useTeamLeads";

// ==================== PUBLIC API ====================

/**
 * Dashboard page showing team performance metrics
 * @public
 */
export default function DashboardPage() {
  const {
    timeRange,
    compareWithPrevious,
    selectedLeadId,
    updateTimeRange,
    toggleComparison,
    updateLeadSelection,
  } = useDashboardFilters();

  // Fetch team leads for the filter dropdown
  const { data: leads = [], isLoading: leadsLoading } = useTeamLeads();

  // Prefetch common time ranges for better UX
  useDashboardPrefetch();

  // Team sheet state
  const [selectedTeamId, setSelectedTeamId] = useState<string | null>(null);
  const [isTeamSheetOpen, setIsTeamSheetOpen] = useState(false);

  const {
    data: dashboardData,
    isLoading,
    error,
  } = useTeamDashboardMetrics({
    timeRange,
    leadIds: selectedLeadId ? [selectedLeadId] : undefined,
    compareWithPrevious,
  });

  // Destructure the response
  const teamMetrics = dashboardData?.teams;
  const organizationUrlKey = dashboardData?.organizationUrlKey;

  // Memoize sorted teams
  const sortedTeams = useMemo(() => {
    return teamMetrics ? [...teamMetrics].sort((a, b) => a.team.name.localeCompare(b.team.name)) : [];
  }, [teamMetrics]);

  // Team edit handlers
  const handleEditTeam = (teamId: string) => {
    setSelectedTeamId(teamId);
    setIsTeamSheetOpen(true);
  };

  const handleCloseTeamSheet = () => {
    setIsTeamSheetOpen(false);
    setSelectedTeamId(null);
  };

  // Create minimal team object for TeamSheet (it will fetch full details)
  const selectedTeam = useMemo(() => {
    if (!selectedTeamId || !teamMetrics) return null;

    const teamData = teamMetrics.find(t => t.team.id === selectedTeamId);
    if (!teamData) return null;

    // Create minimal team object with just enough data for TeamSheet to work
    return {
      id: teamData.team.id,
      name: teamData.team.name,
      key: teamData.team.name.toUpperCase().slice(0, 3), // Fallback for key
      source_id: teamData.team.id,
      source_type: "linear",
      description: null,
      is_visible: teamData.team.is_visible,
      team_lead_id: teamData.team.lead_id,
      organization_id: "", // Will be populated by TeamSheet
      created_at: "",
      updated_at: "",
      team_lead: teamData.team.lead_name ? {
        id: teamData.team.lead_id!,
        name: teamData.team.lead_name,
        email: "",
        avatar_url: null,
      } : null,
      team_members: [], // TeamSheet will fetch this
      stats: {
        totalMembers: teamData.team.member_count,
        contributors: 0, // TeamSheet will fetch this
        viewers: 0, // TeamSheet will fetch this
      },
    };
  }, [selectedTeamId, teamMetrics]);

  if (error) {
    return (
      <div className="flex-1 space-y-6 p-8 pt-6">
        <div className="flex items-center justify-between">
