{"output":"import { Result, ok, err } from "@repo/result";

export type FetcherOptions = {
  method?: "GET" | "POST" | "PUT" | "PATCH" | "DELETE";
  body?: unknown;
  headers?: Record<string, string>;
  signal?: AbortSignal;
};

export async function fetcher<T = unknown, E = string>(
  url: string,
  options: FetcherOptions = {},
): Promise<Result<T, E>> {
  const { method = "GET", body, headers = {}, signal } = options;

  const config: RequestInit = {
    method,
    headers: {
      "Content-Type": "application/json",
      ...headers,
    },
  };

  if (body) {
    config.body = JSON.stringify(body);
  }

  if (signal) {
    config.signal = signal;
  }

  try {
    const response = await fetch(url, config);

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const result = (await response.json()) as any;

    // If the API already returns Result format, return it directly
    if (result && typeof result === "object" && result.ok !== undefined) {
      return result as Result<T, E>;
    }

    // Otherwise wrap in Result format
    if (!response.ok) {
      return err<T, E>(
        (result?.message ||
          result?.error ||
          `Request failed: ${response.status}`) as E,
      );
    }

    return ok<T, E>(result as T);
  } catch (error) {
    return err<T, E>(
      (error instanceof Error ? error.message : "Network error") as E,
    );
  }
}
