import { createHandler } from "@repo/server-utils";
import { getSession } from "@/domain/auth/services/getSession";
import { updateTeamMember, getTeamMember } from "@/domain/teams/services/crudTeamMembers";
import { teamMemberUpdateSchema } from "@/domain/teams/schema/teamMemberSchema";
import { handleTeamMemberRoleChange } from "@/domain/teams/services/handleTeamMemberRoleChange";


const paramsSchema = z.object({
  id: z.string().uuid(),
});

export const PATCH = createHandler({
  auth: getSession,
  paramsSchema,
  bodySchema: teamMemberUpdateSchema,
  handler: async ({ body, params, ctx }) => {
    const id = params.id;

    // STEP 1: Get existing team member to detect changes
    const existingMemberResult = await getTeamMember({
      ctx,
      id,
    });

    if (!existingMemberResult.ok) {
      return existingMemberResult;
    }

    const existingMember = existingMemberResult.data;
    const oldMemberType = existingMember.member_type;

    // STEP 2: Update the team member
    const updateResult = await updateTeamMember({
      ctx,
      id,
      data: body
    });

    if (!updateResult.ok) {
      return updateResult;
    }

    const updatedMember = updateResult.data;
    const newMemberType = updatedMember.member_type;

    // STEP 3: Check if member_type changed and trigger reprocessing if needed
    if (body.member_type && oldMemberType !== newMemberType &&
        oldMemberType && newMemberType &&
        (oldMemberType === 'contributor' || oldMemberType === 'viewer') &&
        (newMemberType === 'contributor' || newMemberType === 'viewer')) {

      // Trigger reprocessing asynchronously (don't wait for it to complete)
      const reprocessingResult = await handleTeamMemberRoleChange({
        data: {
          memberId: existingMember.member_id,
          teamId: existingMember.team_id,
          oldMemberType: oldMemberType as 'contributor' | 'viewer',
          newMemberType: newMemberType as 'contributor' | 'viewer',
          memberName: existingMember.member_id, // Could be enhanced with actual name if available
        },
        ctx,
      });

      if (!reprocessingResult.ok) {
        // Log the error but don't fail the API response
        // The team member update succeeded, reprocessing is a background concern
        console.error("Failed to reprocess team member role change:", reprocessingResult.error);
      }
    }

    return updateResult;
  },
});","metadata":{"exit_code":0,"duration_seconds":0.0}}

