{"output":"import { useState, useMemo } from "react";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
} from "@repo/ui/components/sheet";
import { Label } from "@repo/ui/components/label";
import { Switch } from "@repo/ui/components/switch";
import { Eye, EyeOff } from "lucide-react";
import { TeamLeadCombobox } from "./TeamLeadCombobox";
import { MembersByTypeList } from "./MembersByTypeList";
import { useUpdateTeam } from "../hooks/useUpdateTeam";
import { useUpdateTeamMember } from "../hooks/useUpdateTeamMember";
import { useTeamsWithStats } from "../hooks/useTeamsWithStats";
import type { TeamWithStats } from "@/domain/teams/services/getTeamsWithStats";

// ==================== TYPES ====================

interface TeamSheetProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  team: TeamWithStats | null;
}

// ==================== PUBLIC API ====================

/**
 * Team management sheet with unified view for lead assignment and member management
 * @public
 */
export function TeamSheet({
  isOpen,
  onOpenChange,
  team,
}: TeamSheetProps) {
  const [isLoading, setIsLoading] = useState(false);
  const updateTeamMutation = useUpdateTeam();
  const updateMemberMutation = useUpdateTeamMember();

  // Fetch fresh team data to ensure we have the latest member info
  const { data: teamsData } = useTeamsWithStats();

  // Get the current team with fresh data, fallback to prop
  const currentTeam = useMemo(() => {
    if (!team || !teamsData?.data) return team;
    return teamsData.data.find(t => t.id === team.id) || team;
  }, [team, teamsData?.data]);

  // Get team members from fresh data
  const allMembers = useMemo(() => {
    return currentTeam?.team_members || [];
  }, [currentTeam?.team_members]);

  const handleLeadChange = async (memberId: string | null) => {
    if (!currentTeam) return;

    setIsLoading(true);
    try {
      await updateTeamMutation.mutateAsync({
        id: currentTeam.id,
        data: { team_lead_id: memberId },
      });
    } finally {
      setIsLoading(false);
    }
  };


  const handleTypeChange = async (memberId: string, newType: "contributor" | "viewer") => {
    setIsLoading(true);
    try {
      await updateMemberMutation.mutateAsync({
        id: memberId,
        data: { member_type: newType },
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleVisibilityChange = async (isVisible: boolean) => {
    if (!currentTeam) return;

    setIsLoading(true);
    try {
      await updateTeamMutation.mutateAsync({
        id: currentTeam.id,
        data: { is_visible: isVisible },
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (!currentTeam) return null;

  return (
    <Sheet open={isOpen} onOpenChange={onOpenChange}>
      <SheetContent className="w-[600px] sm:max-w-[600px] overflow-y-auto p-6">
        <SheetHeader>
          <SheetTitle className="flex items-center gap-2">
            <span className="text-xs font-mono bg-muted px-2 py-1 rounded">
              {currentTeam.key}
            </span>
            {currentTeam.name}
          </SheetTitle>
          <SheetDescription>
            Manage team lead and member settings
          </SheetDescription>
        </SheetHeader>

        <div className="mt-6 space-y-6">
          {/* Team Visibility Section */}
          <div className="flex items-center justify-between p-3 border rounded-lg">
            <div className="flex items-center gap-3">
              {currentTeam.is_visible ? (
                <Eye className="h-4 w-4 text-green-600" />
              ) : (
                <EyeOff className="h-4 w-4 text-gray-500" />
              )}
              <div>
                <Label className="text-sm font-medium">Team Visibility</Label>
                <p className="text-xs text-muted-foreground mt-0.5">
                  {currentTeam.is_visible
                    ? "Team is visible to all organization members"
                    : "Team is hidden from organization views"
                  }
                </p>
              </div>
            </div>
            <Switch
              checked={currentTeam.is_visible}
              onCheckedChange={handleVisibilityChange}
              disabled={isLoading}
            />
          </div>

          {/* Team Lead Section */}
          <div>
            <Label className="text-sm font-medium mb-2 block">Team Lead</Label>
            <TeamLeadCombobox
              currentLeadId={currentTeam.team_lead_id}
              members={allMembers}
              onSelect={handleLeadChange}
              disabled={isLoading}
            />
            {currentTeam.team_lead && (
              <p className="text-xs text-muted-foreground mt-1">
                Current lead: {currentTeam.team_lead.name}
              </p>
            )}
          </div>

          {/* Team Members Section */}
          <div className="space-y-6">
            {/* Contributors */}
            <MembersByTypeList
              members={allMembers}
              type="contributor"
              title="Contributors"
              onTypeChange={handleTypeChange}
              isLoading={isLoading}
            />

            {/* Viewers */}
            <MembersByTypeList
              members={allMembers}
              type="viewer"
              title="Viewers"
              onTypeChange={handleTypeChange}
              isLoading={isLoading}
            />

            {/* Empty state */}
            {allMembers.length === 0 && (
              <div className="text-center py-8">
                <p className="text-muted-foreground">No team members found</p>
                <p className="text-sm text-muted-foreground mt-1">
                  Members will appear here once they are added to the team
                </p>
              </div>
            )}
          </div>

          {/* Team Stats Summary */}
          {allMembers.length > 0 && (
            <div className="border-t pt-4">
              <h4 className="text-sm font-medium mb-2">Team Summary</h4>
              <div className="text-sm text-muted-foreground space-y-1">
                <p>Total members: {currentTeam.stats.totalMembers}</p>
                <p>Contributors: {currentTeam.stats.contributors}</p>
                <p>Viewers: {currentTeam.stats.viewers}</p>
              </div>
            </div>
          )}
        </div>
      </SheetContent>
    </Sheet>
  );
}","metadata":{"exit_code":0,"duration_seconds":0.0}}

{"output":"import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@repo/ui/hooks/use-toast";
import type { TeamWithStats } from "@/domain/teams/services/getTeamsWithStats";

interface UpdateTeamData {
  id: string;
  data: {
    name?: string;
    key?: string;
    description?: string;
    team_lead_id?: string | null;
    is_visible?: boolean;
  };
}

// ==================== PRIVATE HELPERS ====================

/**
 * Update a specific team in the cached teams list
 * @private
 */
function updateTeamInCache(
  teamsData: { data: TeamWithStats[] } | undefined,
  teamId: string,
  updateData: UpdateTeamData["data"]
): { data: TeamWithStats[] } | undefined {
  if (!teamsData?.data) return teamsData;

  const updatedTeams = teamsData.data.map((team) => {
    if (team.id !== teamId) return team;

    // Find the new team lead info if team_lead_id is being updated
    let newTeamLead = team.team_lead;
    if ("team_lead_id" in updateData) {
      if (updateData.team_lead_id === null) {
        newTeamLead = null;
      } else if (
        updateData.team_lead_id &&
        updateData.team_lead_id !== team.team_lead_id
      ) {
        // Find the member info from team_members array
        const leadMember = team.team_members.find(
          (member) => member.id === updateData.team_lead_id
        );
        if (leadMember) {
          newTeamLead = {
            id: leadMember.id,
            name: leadMember.name,
            email: leadMember.email,
            avatar_url: leadMember.avatar_url,
          };
        }
      }
    }

    return {
      ...team,
      ...updateData,
      team_lead: newTeamLead,
    };
  });

  return { data: updatedTeams };
}

export function useUpdateTeam() {
  const queryClient = useQueryClient();
  const toast = useToast();

  return useMutation({
    mutationFn: async ({ id, data }: UpdateTeamData) => {
      const response = await fetch(`/api/teams/${id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to update team");
      }

      return response.json();
    },
    onMutate: async ({ id, data }) => {
      const queryKey = ["teams", "with-stats"];

      // Cancel any outgoing refetches
      await queryClient.cancelQueries({ queryKey });

      // Snapshot the previous value
      const previousTeams = queryClient.getQueryData(queryKey);

      // Optimistically update the cache
      queryClient.setQueryData(
        queryKey,
        (oldData: { data: TeamWithStats[] } | undefined) => {
          return updateTeamInCache(oldData, id, data);
        }
      );

      // Return context object with the snapshotted value
      return { previousTeams };
    },
    onError: (error: Error, variables, context) => {
      // If the mutation fails, use the context returned from onMutate to roll back
      if (context?.previousTeams) {
        queryClient.setQueryData(
          ["teams", "with-stats"],
          context.previousTeams
        );
      }

      toast.error(error.message || "Failed to update team");
    },
    onSuccess: () => {
      toast.success("Team updated successfully");
    },
    onSettled: () => {
      // Always refetch after error or success to ensure consistency
      queryClient.invalidateQueries({
        queryKey: ["teams", "with-stats"],
      });
      queryClient.invalidateQueries({
        queryKey: ["teams"],
      });
      // Invalidate dashboard queries to refresh team metrics
      queryClient.invalidateQueries({
        queryKey: ["dashboard"],
      });
    },
  });
}
