{"output":"import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@repo/ui/hooks/use-toast";

interface UpdateTeamMemberData {
  id: string;
  data: {
    member_type?: "contributor" | "viewer";
    role?: string;
    is_active?: boolean;
  };
}

export function useUpdateTeamMember() {
  const queryClient = useQueryClient();
  const toast = useToast();

  return useMutation({
    mutationFn: async ({ id, data }: UpdateTeamMemberData) => {
      const response = await fetch(`/api/team-members/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to update team member');
      }

      return response.json();
    },
    onSuccess: async () => {
      // Invalidate and refetch teams queries immediately
      await queryClient.invalidateQueries({
        queryKey: ['teams'],
        refetchType: 'active',
      });

      await queryClient.invalidateQueries({
        queryKey: ['team-members'],
        refetchType: 'active',
      });

      // Invalidate dashboard queries to refresh team metrics
      await queryClient.invalidateQueries({
        queryKey: ['dashboard'],
        refetchType: 'active',
      });

      toast.success('Team member updated successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message || 'Failed to update team member');
    },
  });
}","metadata":{"exit_code":0,"duration_seconds":0.0}}

{"output":"import { useQuery } from "@tanstack/react-query";
import { fetcher } from "@repo/utils/fetcher";
import type { TeamWithStats } from "@/domain/teams/services/getTeamsWithStats";

interface UseTeamWithStatsOptions {
  enabled?: boolean;
  initialData?: TeamWithStats;
}

export function useTeamWithStats(
  teamId: string | null | undefined,
  options: UseTeamWithStatsOptions = {},
) {
  const { enabled = true, initialData } = options;

  return useQuery<TeamWithStats>({
    queryKey: ["team", "with-stats", teamId],
    enabled: Boolean(teamId) && enabled,
    staleTime: 30_000,
    keepPreviousData: true,
    initialData,
    queryFn: async () => {
      const result = await fetcher<TeamWithStats, string>(
        `/api/teams/${teamId}?include=stats`,
      );

      if (!result.ok) {
        throw new Error(result.error);
      }

      return result.data;
    },
  });
}
