{"output":"import { useMemo } from "react";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
} from "@repo/ui/components/sheet";
import { Label } from "@repo/ui/components/label";
import { Switch } from "@repo/ui/components/switch";
import { Eye, EyeOff } from "lucide-react";
import { TeamLeadCombobox } from "./TeamLeadCombobox";
import { MembersByTypeList } from "./MembersByTypeList";
import { useUpdateTeam } from "../hooks/useUpdateTeam";
import { useUpdateTeamMember } from "../hooks/useUpdateTeamMember";
import { useTeamWithStats } from "../hooks/useTeamWithStats";
import type { TeamWithStats } from "@/domain/teams/services/getTeamsWithStats";

// ==================== TYPES ====================

interface TeamSheetProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  teamId: string | null;
  initialTeam?: TeamWithStats | null;
}

// ==================== PUBLIC API ====================

/**
 * Team management sheet with unified view for lead assignment and member management
 * @public
 */
export function TeamSheet({
  isOpen,
  onOpenChange,
  teamId,
  initialTeam = null,
}: TeamSheetProps) {
  const updateTeamMutation = useUpdateTeam(teamId);
  const updateMemberMutation = useUpdateTeamMember(teamId);

  const {
    data: fetchedTeam,
    isLoading: isTeamLoading,
    isFetching,
  } = useTeamWithStats(teamId, {
    enabled: isOpen,
    initialData: initialTeam ?? undefined,
  });

  const currentTeam = useMemo<TeamWithStats | null>(() => {
    if (fetchedTeam) return fetchedTeam;
    if (initialTeam) return initialTeam;
    return null;
  }, [fetchedTeam, initialTeam]);

  const allMembers = useMemo(
    () => currentTeam?.team_members ?? [],
    [currentTeam?.team_members],
  );

  const isMutating = updateTeamMutation.isPending || updateMemberMutation.isPending;
  const isBusy = isMutating || isFetching;

  const showNoSelection = !teamId;
  const showLoading = !currentTeam && (isTeamLoading || isFetching);

  const handleLeadChange = async (memberId: string | null) => {
    if (!currentTeam) return;

    await updateTeamMutation.mutateAsync({
      id: currentTeam.id,
      data: { team_lead_id: memberId },
    });
  };


  const handleTypeChange = async (memberId: string, newType: "contributor" | "viewer") => {
    await updateMemberMutation.mutateAsync({
      id: memberId,
      data: { member_type: newType },
    });
  };

  const handleVisibilityChange = async (isVisible: boolean) => {
    if (!currentTeam) return;

    await updateTeamMutation.mutateAsync({
      id: currentTeam.id,
      data: { is_visible: isVisible },
    });
  };

  return (
    <Sheet open={isOpen} onOpenChange={onOpenChange}>
      <SheetContent className="w-[600px] sm:max-w-[600px] overflow-y-auto p-6">
        {showNoSelection ? (
          <EmptyState
            message="No team selected"
            description="Select a team to manage its members and visibility."
          />
        ) : showLoading ? (
          <LoadingState />
        ) : currentTeam ? (
          <TeamSheetContent
            team={currentTeam}
            allMembers={allMembers}
            isBusy={isBusy}
            onLeadChange={handleLeadChange}
            onMemberTypeChange={handleTypeChange}
            onVisibilityChange={handleVisibilityChange}
          />
        ) : (
          <EmptyState
            message="Unable to load this team"
            description="Please close the sheet and try again."
          />
        )}
      </SheetContent>
    </Sheet>
  );
}

// ==================== PRIVATE HELPERS ====================

interface TeamSheetContentProps {
  team: TeamWithStats;
  allMembers: TeamWithStats["team_members"];
  isBusy: boolean;
  onLeadChange: (memberId: string | null) => void;
  onMemberTypeChange: (memberId: string, newType: "contributor" | "viewer") => void;
  onVisibilityChange: (isVisible: boolean) => void;
}

function TeamSheetContent({
  team,
  allMembers,
  isBusy,
  onLeadChange,
  onMemberTypeChange,
  onVisibilityChange,
}: TeamSheetContentProps) {
  return (
    <>
      <SheetHeader>
        <SheetTitle className="flex items-center gap-2">
          <span className="text-xs font-mono bg-muted px-2 py-1 rounded">
            {team.key}
          </span>
          {team.name}
        </SheetTitle>
        <SheetDescription>
          Manage team lead and member settings
        </SheetDescription>
      </SheetHeader>

      <div className="mt-6 space-y-6">
        {/* Team Visibility Section */}
        <div className="flex items-center justify-between p-3 border rounded-lg">
          <div className="flex items-center gap-3">
            {team.is_visible ? (
              <Eye className="h-4 w-4 text-green-600" />
            ) : (
              <EyeOff className="h-4 w-4 text-gray-500" />
            )}
            <div>
              <Label className="text-sm font-medium">Team Visibility</Label>
              <p className="text-xs text-muted-foreground mt-0.5">
                {team.is_visible
                  ? "Team is visible to all organization members"
                  : "Team is hidden from organization views"
                }
              </p>
            </div>
          </div>
          <Switch
            checked={team.is_visible}
            onCheckedChange={onVisibilityChange}
            disabled={isBusy}
          />
        </div>

        {/* Team Lead Section */}
        <div>
          <Label className="text-sm font-medium mb-2 block">Team Lead</Label>
          <TeamLeadCombobox
            currentLeadId={team.team_lead_id}
            members={allMembers}
            onSelect={onLeadChange}
            disabled={isBusy}
          />
          {team.team_lead && (
            <p className="text-xs text-muted-foreground mt-1">
              Current lead: {team.team_lead.name}
            </p>
          )}
        </div>

        {/* Team Members Section */}
        <div className="space-y-6">
          {/* Contributors */}
          <MembersByTypeList
            members={allMembers}
            type="contributor"
            title="Contributors"
            onTypeChange={onMemberTypeChange}
            isLoading={isBusy}
          />

          {/* Viewers */}
          <MembersByTypeList
            members={allMembers}
            type="viewer"
            title="Viewers"
            onTypeChange={onMemberTypeChange}
            isLoading={isBusy}
          />

          {/* Empty state */}
","metadata":{"exit_code":0,"duration_seconds":0.0}}

{"output":"          {/* Empty state */}
          {allMembers.length === 0 && (
            <div className="text-center py-8">
              <p className="text-muted-foreground">No team members found</p>
              <p className="text-sm text-muted-foreground mt-1">
                Members will appear here once they are added to the team
              </p>
            </div>
          )}
        </div>

        {/* Team Stats Summary */}
        {allMembers.length > 0 && (
          <div className="border-t pt-4">
            <h4 className="text-sm font-medium mb-2">Team Summary</h4>
            <div className="text-sm text-muted-foreground space-y-1">
              <p>Total members: {team.stats.totalMembers}</p>
              <p>Contributors: {team.stats.contributors}</p>
              <p>Viewers: {team.stats.viewers}</p>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

function LoadingState() {
  return (
    <>
      <SheetHeader>
        <SheetTitle>Loading team…</SheetTitle>
        <SheetDescription>Fetching the latest team details.</SheetDescription>
      </SheetHeader>
      <div className="py-12 text-center text-muted-foreground">
        Loading team details…
      </div>
    </>
  );
}

function EmptyState({
  message,
  description,
}: {
  message: string;
  description?: string;
}) {
  return (
    <>
      <SheetHeader>
        <SheetTitle>Team management</SheetTitle>
        {description && <SheetDescription>{description}</SheetDescription>}
      </SheetHeader>
      <div className="py-12 text-center text-muted-foreground">
        {message}
      </div>
    </>
  );
}
