{"output":"import { ok, err } from "@repo/result";
import type { ServiceParams } from "@repo/services";
import { db } from "@/lib/supabase/db";
import { getTeamMembersWithJoinedDetails } from "@/domain/shared/joins/joinTeamMembers";
import { createMemberLookupMap } from "@/domain/shared/joins/joinMembers";
import type { TeamMemberWithDetails as SharedTeamMemberWithDetails } from "@/domain/shared/joins/types";

// ==================== TYPES ====================


export interface TeamStats {
  totalMembers: number;
  contributors: number;
  viewers: number;
}

export interface TeamLead {
  id: string;
  name: string;
  email: string;
  avatar_url: string | null;
}

export interface TeamMemberWithDetails {
  id: string; // team_member record UUID
  member_id: string; // member ID (linear-user-123)
  name: string;
  email: string;
  avatar_url: string | null;
  member_type: "contributor" | "viewer";
  is_active: boolean;
  role: string;
}

export interface TeamWithStats {
  id: string; // Linear ID format (e.g., "linear-team-123")
  name: string;
  key: string;
  source_id: string; // Original Linear team ID
  source_type: string; // "linear"
  description: string | null;
  is_visible: boolean;
  team_lead_id: string | null;
  organization_id: string;
  created_at: string;
  updated_at: string;
  team_lead: TeamLead | null;
  team_members: TeamMemberWithDetails[];
  stats: TeamStats;
}

// ==================== PUBLIC API ====================

/**
 * Get all teams with their statistics and team lead information
 * @public
 */
export async function getTeamsWithStats({ ctx }: ServiceParams<void>) {
  try {
    // First get all teams for the organization
    const { data: teams, error: teamsError } = await db
      .from('teams')
      .select('*')
      .eq('organization_id', ctx.organization_id)
      .order('name');

    if (teamsError) {
      console.error('Error fetching teams:', teamsError);
      return err(new Error(`Failed to fetch teams: ${teamsError.message}`));
    }

    if (!teams || teams.length === 0) {
      return ok([]);
    }

    // Get team members with joined details for all teams using new join helpers
    const teamMembersByTeam = new Map<string, SharedTeamMemberWithDetails[]>();

    for (const team of teams) {
      const teamMembersResult = await getTeamMembersWithJoinedDetails({
        teamId: team.id,
        includeInactive: false, // Only active members
        ctx,
      });

      if (teamMembersResult.ok) {
        teamMembersByTeam.set(team.id, teamMembersResult.data);
      } else {
        console.error(`Error fetching team members for team ${team.id}:`, teamMembersResult.error);
        teamMembersByTeam.set(team.id, []);
      }
    }

    // Get team lead details for teams that have a team_lead_id
    // Note: team_lead_id refers to team_members.id, not members.id
    const teamLeadIds = teams.filter(t => t.team_lead_id).map(t => t.team_lead_id!);
    let teamLeadDetailsMap: Record<string, { id: string; name: string; email: string; avatar_url: string | null }> = {};

    if (teamLeadIds.length > 0) {
      // Get team lead member records to map team_member.id -> member_id
      const { data: teamLeadMembers, error: teamLeadMembersError } = await db
        .from('team_members')
        .select('id, member_id')
        .in('id', teamLeadIds);

      if (teamLeadMembersError) {
        console.error('Error fetching team lead member records:', teamLeadMembersError);
      } else if (teamLeadMembers && teamLeadMembers.length > 0) {
        // Get member details using the join helper
        const memberIds = teamLeadMembers.map(tm => tm.member_id);
        const memberLookupResult = await createMemberLookupMap({
          memberIds,
          ctx,
        });

        if (memberLookupResult.ok) {
          const memberLookup = memberLookupResult.data;

          // Create mapping from team_member.id to member details
          teamLeadDetailsMap = teamLeadMembers.reduce((acc, teamMember) => {
            const memberDetails = memberLookup.get(teamMember.member_id);
            if (memberDetails) {
              acc[teamMember.id] = memberDetails; // Use team_member.id as key
            }
            return acc;
          }, {} as Record<string, { id: string; name: string; email: string; avatar_url: string | null }>);
        }
      }
    }

    const teamsWithStats: TeamWithStats[] = teams.map(team => {
      const sharedMembers = teamMembersByTeam.get(team.id) || [];

      const activeMembers = mapSharedMembersToTeamMembers(sharedMembers);

      // Get team lead details if team_lead_id exists
      const teamLead = team.team_lead_id ? teamLeadDetailsMap[team.team_lead_id] : null;

      return {
        id: team.id,
        name: team.name,
        key: team.key,
        source_id: team.source_id,
        source_type: team.source_type,
        description: (team.source_data as Record<string, unknown>)?.description as string || null,
        is_visible: team.is_visible ?? true,
        team_lead_id: team.team_lead_id,
        organization_id: team.organization_id,
        created_at: team.created_at,
        updated_at: team.updated_at,
        team_lead: teamLead ? {
          id: teamLead.id,
          name: teamLead.name,
          email: teamLead.email,
          avatar_url: teamLead.avatar_url,
        } : null,
        team_members: activeMembers,
        stats: calculateTeamStats(activeMembers),
      };
    });

    return ok(teamsWithStats);
  } catch (error) {
    console.error('Unexpected error in getTeamsWithStats:', error);
    return err(new Error('An unexpected error occurred while fetching teams'));
  }
}

// ==================== PUBLIC API (Single Team) ====================

export async function getTeamWithStats({
  ctx,
  data,
}: ServiceParams<{ teamId: string }>) {
  try {
    const { teamId } = data;

    const { data: team, error: teamError } = await db
      .from('teams')
      .select('*')
      .eq('organization_id', ctx.organization_id)
      .eq('id', teamId)
      .maybeSingle();

    if (teamError) {
      console.error(`Error fetching team ${teamId}:`, teamError);
      return err(new Error(`Failed to fetch team: ${teamError.message}`));
    }

    if (!team) {
      return err(new Error('Team not found'));
    }

    const teamMembersResult = await getTeamMembersWithJoinedDetails({
      teamId: team.id,
      includeInactive: false,
      ctx,
    });

    const sharedMembers = teamMembersResult.ok ? teamMembersResult.data : [];

    if (!teamMembersResult.ok) {
      console.error(
        `Error fetching team members for team ${team.id}:`,
        teamMembersResult.error,
      );
    }

    const activeMembers = mapSharedMembersToTeamMembers(sharedMembers);

    const teamLead = team.team_lead_id
      ? await getSingleTeamLeadDetails(team.team_lead_id, ctx)
      : null;

    const teamWithStats: TeamWithStats = {
      id: team.id,
      name: team.name,
      key: team.key,
      source_id: team.source_id,
      source_type: team.source_type,
      description:
        ((team.source_data as Record<string, unknown>)?.description as string) ||
        null,
      is_visible: team.is_visible ?? true,
      team_lead_id: team.team_lead_id,
      organization_id: team.organization_id,
      created_at: team.created_at,
      updated_at: team.updated_at,
      team_lead,
      team_members: activeMembers,
      stats: calculateTeamStats(activeMembers),
    };

    return ok(teamWithStats);
  } catch (error) {
    console.error('Unexpected error in getTeamWithStats:', error);
    return err(new Error('An unexpected error occurred while fetching team'));
  }
}

