{"output":"
// ==================== PRIVATE HELPERS ====================

/**
 * Calculate team statistics from team members array
 * @private
 */
function calculateTeamStats(teamMembers: TeamMemberWithDetails[]): TeamStats {
  const totalMembers = teamMembers.length;
  const contributors = teamMembers.filter(m => m.member_type === 'contributor').length;
  const viewers = teamMembers.filter(m => m.member_type === 'viewer').length;

  return {
    totalMembers,
    contributors,
    viewers,
  };
}

function mapSharedMembersToTeamMembers(
  teamMembers: SharedTeamMemberWithDetails[],
): TeamMemberWithDetails[] {
  return teamMembers.map(member => ({
    id: member.team_member_id,
    member_id: member.id,
    name: member.name,
    email: member.email,
    avatar_url: member.avatar_url,
    member_type: member.member_type,
    is_active: member.is_active,
    role: member.role,
  }));
}

async function getSingleTeamLeadDetails(
  teamMemberId: string,
  ctx: ServiceParams<void>["ctx"],
): Promise<TeamLead | null> {
  const { data: teamMember, error: teamMemberError } = await db
    .from('team_members')
    .select('id, member_id')
    .eq('id', teamMemberId)
    .maybeSingle();

  if (teamMemberError) {
    console.error('Error fetching team lead member record:', teamMemberError);
    return null;
  }

  if (!teamMember) {
    return null;
  }

  const memberLookupResult = await createMemberLookupMap({
    memberIds: [teamMember.member_id],
    ctx,
  });

  if (!memberLookupResult.ok) {
    console.error(
      'Error fetching team lead member details:',
      memberLookupResult.error,
    );
    return null;
  }

  const memberDetails = memberLookupResult.data.get(teamMember.member_id);

  if (!memberDetails) {
    return null;
  }

  return {
    id: memberDetails.id,
    name: memberDetails.name,
    email: memberDetails.email,
    avatar_url: memberDetails.avatar_url,
  };
}
