    16	// ==================== PRIVATE HELPERS ====================
    17	
    18	/**
    19	 * Update a specific team in the cached teams list
    20	 * @private
    21	 */
    22	function applyTeamUpdate(
    23	  team: TeamWithStats,
    24	  updateData: UpdateTeamData["data"],
    25	): TeamWithStats {
    26	  let newTeamLead = team.team_lead;
    27	  if ("team_lead_id" in updateData) {
    28	    if (updateData.team_lead_id === null) {
    29	      newTeamLead = null;
    30	    } else if (
    31	      updateData.team_lead_id &&
    32	      updateData.team_lead_id !== team.team_lead_id
    33	    ) {
    34	      const leadMember = team.team_members.find(
    35	        (member) => member.id === updateData.team_lead_id
    36	      );
    37	      if (leadMember) {
    38	        newTeamLead = {
    39	          id: leadMember.id,
    40	          name: leadMember.name,
    41	          email: leadMember.email,
    42	          avatar_url: leadMember.avatar_url,
    43	        };
    44	      }
    45	    }
    46	  }
    47	
    48	  return {
    49	    ...team,
    50	    ...updateData,
    51	    team_lead: newTeamLead,
    52	  };
    53	}
    54	
    55	function updateTeamListCache(
    56	  teamsData: { data: TeamWithStats[] } | undefined,
    57	  teamId: string,
    58	  updateData: UpdateTeamData["data"],
    59	): { data: TeamWithStats[] } | undefined {
    60	  if (!teamsData?.data) return teamsData;
    61	
    62	  const updatedTeams = teamsData.data.map((team) => {
    63	    if (team.id !== teamId) return team;
    64	    return applyTeamUpdate(team, updateData);
    65	  });
    66	
    67	  return { data: updatedTeams };
    68	}
    69	
    70	type UpdateTeamContext = {
    71	  previousTeams?: { data: TeamWithStats[] } | undefined;
    72	  previousTeam?: TeamWithStats;
    73	  listKey: unknown[];
    74	  singleKey: unknown[];
    75	};
    76	
    77	export function useUpdateTeam(teamId?: string | null) {
    78	  const queryClient = useQueryClient();
    79	  const toast = useToast();
    80	
    81	  return useMutation<unknown, Error, UpdateTeamData, UpdateTeamContext>({
    82	    mutationKey: ["team", "update", teamId ?? "unknown"],
    83	    mutationFn: async ({ id, data }: UpdateTeamData) => {
    84	      const response = await fetch(`/api/teams/${id}`, {
    85	        method: "PATCH",
    86	        headers: {
    87	          "Content-Type": "application/json",
    88	        },
    89	        body: JSON.stringify(data),
    90	      });
    91	
    92	      if (!response.ok) {
    93	        const error = await response.json();
    94	        throw new Error(error.message || "Failed to update team");
    95	      }
    96	
    97	      return response.json();
    98	    },
    99	    onMutate: async ({ id, data }) => {
   100	      const listKey = ["teams", "with-stats"];
   101	      const singleKey = ["team", "with-stats", id];
   102	
   103	      await Promise.all([
   104	        queryClient.cancelQueries({ queryKey: listKey }),
   105	        queryClient.cancelQueries({ queryKey: singleKey }),
   106	      ]);
   107	
   108	      const previousTeams = queryClient.getQueryData(listKey);
   109	      const previousTeam = queryClient.getQueryData<TeamWithStats | undefined>(singleKey);
   110	
   111	      queryClient.setQueryData(
   112	        listKey,
   113	        (oldData: { data: TeamWithStats[] } | undefined) =>
   114	          updateTeamListCache(oldData, id, data),
   115	      );
   116	
   117	      queryClient.setQueryData(singleKey, (oldTeam: TeamWithStats | undefined) => {
   118	        if (!oldTeam) return oldTeam;
   119	        return applyTeamUpdate(oldTeam, data);
   120	      });
   121	
   122	      return { previousTeams, previousTeam, listKey, singleKey };
   123	    },
   124	    onError: (error: Error, _variables, context) => {
   125	      // If the mutation fails, use the context returned from onMutate to roll back
   126	      if (context?.previousTeams && context?.listKey) {
   127	        queryClient.setQueryData(context.listKey, context.previousTeams);
   128	      }
   129	
   130	      if (context?.previousTeam && context.singleKey) {
   131	        queryClient.setQueryData(context.singleKey, context.previousTeam);
   132	      }
   133	
   134	      toast.error(error.message || "Failed to update team");
   135	    },
   136	    onSuccess: () => {
   137	      toast.success("Team updated successfully");
   138	    },
   139	    onSettled: (_result, _error, variables, context) => {
   140	      if (variables) {
   141	        queryClient.invalidateQueries({
   142	          queryKey: ["team", "with-stats", variables.id],
   143	          refetchType: "inactive",
   144	        });
   145	      }
   146	
   147	      if (context?.listKey) {
   148	        queryClient.invalidateQueries({
   149	          queryKey: context.listKey,
   150	          refetchType: "inactive",
   151	        });
   152	      }
   153	
   154	      // Invalidate dashboard queries to refresh team metrics
   155	      queryClient.invalidateQueries({
   156	        queryKey: ["dashboard"],
   157	        refetchType: "active",
   158	      });
   159	    },
   160	  });
   161	}
