   117	
   118	function updateMemberInTeam(
   119	  team: TeamWithStats,
   120	  memberId: string,
   121	  data: UpdateTeamMemberData['data'],
   122	): TeamWithStats {
   123	  const updatedMembers = team.team_members.map((member) => {
   124	    if (member.id !== memberId) return member;
   125	    return {
   126	      ...member,
   127	      ...data,
   128	    } satisfies TeamMemberWithDetails;
   129	  });
   130	
   131	  const stats = recalculateStats(updatedMembers);
   132	
   133	  return {
   134	    ...team,
   135	    team_members: updatedMembers,
   136	    stats,
   137	  };
   138	}
   139	
   140	function replaceTeamInList(
   141	  teamsData: { data: TeamWithStats[] } | undefined,
   142	  updatedTeam: TeamWithStats,
   143	): { data: TeamWithStats[] } | undefined {
   144	  if (!teamsData?.data) return teamsData;
   145	
   146	  const updatedTeams = teamsData.data.map((team) =>
   147	    team.id === updatedTeam.id ? updatedTeam : team,
   148	  );
   149	
   150	  return { data: updatedTeams };
   151	}
   152	
   153	function recalculateStats(members: TeamMemberWithDetails[]): TeamStats {
   154	  const totalMembers = members.length;
   155	  const contributors = members.filter((member) => member.member_type === 'contributor').length;
   156	  const viewers = members.filter((member) => member.member_type === 'viewer').length;
   157	
   158	  return {
   159	    totalMembers,
   160	    contributors,
   161	    viewers,
   162	  };
   163	}
