{"output":"/**
 * Manual Join Service for Team Members
 *
 * WHY MANUAL JOINS?
 * ==================
 * Supabase can't join team_members -> members without FK relationships.
 *
 * DOUBLE JOIN PATTERN:
 * ====================
 * Team members require a two-step join:
 * 1. team_members table -> get member_id and member_type/role
 * 2. members table -> get member details (name, email, etc.)
 *
 * This is necessary because:
 * - team_members stores the relationship and role info
 * - members stores the actual person details
 * - No FK constraint exists between them
 */

import { ok, fail, type Result } from "@repo/result";
import { db } from "@/lib/supabase/db";
import type {
  TeamMemberWithDetails,
  CollectionWithTeamMembers,
} from "./types";
import { createMemberLookupMap } from "./joinMembers";
import { createLookupMapUtility } from "./joinCollections";

// ==================== PUBLIC API ====================

/**
 * Join team member details (with member_type, role) to collection
 *
 * @example
 * const result = await joinTeamMembersToCollection({
 *   collection: contributorStats,
 *   memberKey: 'assignee_id',
 *   teamId: 'team-123',
 *   joinAs: 'teamMember',
 *   ctx
 * });
 *
 * @public
 */
export async function joinTeamMembersToCollection<T>({
  collection,
  memberKey,
  teamId,
  joinAs = 'teamMember',
  includeInactive = false,
  defaultValue = null,
  ctx,
}: {
  collection: T[];
  memberKey: string;
  teamId: string;
  joinAs?: string;
  includeInactive?: boolean;
  defaultValue?: TeamMemberWithDetails | null;
  ctx: { organization_id: string };
}): Promise<Result<CollectionWithTeamMembers<T>, Error>> {
  try {
    if (collection.length === 0) {
      return ok({
        items: [],
        teamMemberLookup: new Map(),
      });
    }

    // Get team members with joined details
    const teamMembersResult = await getTeamMembersWithJoinedDetails({
      teamId,
      includeInactive,
      ctx,
    });

    if (!teamMembersResult.ok) {
      return teamMembersResult;
    }

    const teamMembers = teamMembersResult.data;
    const teamMemberLookup = createLookupMapUtility(teamMembers, 'id') as Map<string, TeamMemberWithDetails>;

    // Join team member data to collection
    const items = collection.map((item) => {
      const memberId = (item as Record<string, unknown>)[memberKey] as string;
      const teamMember = memberId ? teamMemberLookup.get(memberId) : null;

      return {
        ...item,
        [joinAs]: teamMember ?? defaultValue,
      };
    });

    return ok({
      items,
      teamMemberLookup,
    });
  } catch (error) {
    return fail(`Failed to join team members: ${error instanceof Error ? error.message : "Unknown error"}`);
  }
}

/**
 * Get team members with joined member details
 *
 * This performs the double join:
 * team_members -> members
 *
 * @public
 */
export async function getTeamMembersWithJoinedDetails({
  teamId,
  includeInactive = false,
  ctx,
}: {
  teamId: string;
  includeInactive?: boolean;
  ctx: { organization_id: string };
}): Promise<Result<TeamMemberWithDetails[], Error>> {
  try {
    // Step 1: Fetch team members (relationship table)
    let query = db
      .from("team_members")
      .select("id, member_id, member_type, role, is_active")
      .eq("team_id", teamId);

    if (!includeInactive) {
      query = query.eq("is_active", true);
    }

    const { data: teamMembersData, error: teamMembersError } = await query;

    if (teamMembersError) {
      return fail(`Failed to fetch team members: ${teamMembersError.message}`);
    }

    if (!teamMembersData || teamMembersData.length === 0) {
      return ok([]);
    }

    // Step 2: Extract member IDs and fetch member details
    const memberIds = teamMembersData.map(tm => tm.member_id).filter(Boolean);

    if (memberIds.length === 0) {
      return ok([]);
    }

    const memberLookupResult = await createMemberLookupMap({
      memberIds,
      ctx,
    });

    if (!memberLookupResult.ok) {
      return memberLookupResult;
    }

    const memberLookup = memberLookupResult.data;

    // Step 3: Combine team member data with member details
    const teamMembersWithDetails: TeamMemberWithDetails[] = teamMembersData
      .map(teamMember => {
        const memberDetail = memberLookup.get(teamMember.member_id);
        if (!memberDetail) return null;

        return {
          ...memberDetail,
          team_member_id: teamMember.id,
          member_type: teamMember.member_type as 'contributor' | 'viewer',
          role: teamMember.role,
          is_active: teamMember.is_active,
        };
      })
      .filter((member): member is TeamMemberWithDetails => member !== null);

    return ok(teamMembersWithDetails);
  } catch (error) {
    return fail(`Failed to get team members with details: ${error instanceof Error ? error.message : "Unknown error"}`);
  }
}

