type InferAuthType<T> = T extends () => Promise<Result<infer U, any>>
  ? U
  : never;
type InferSchemaType<T> = T extends z.ZodType<infer U> ? U : any;

// Service context for multi-tenant operations
export interface ServiceContext {
  organization_id: string;
