interface CreateHandlerOptions<
  TAuth extends (() => Promise<Result<any, any>>) | undefined,
  TParamsSchema extends z.ZodType<any> | undefined,
  TBodySchema extends z.ZodType<any> | undefined,
  TQuerySchema extends z.ZodType<any> | undefined,
  TResult,
  TRBAC = any,
> {
  auth?: TAuth;
  rbac?: TAuth extends undefined
    ? never
    : (session: InferAuthType<NonNullable<TAuth>>) => TRBAC;
  paramsSchema?: TParamsSchema;
  bodySchema?: TBodySchema;
  querySchema?: TQuerySchema;

  authorize?: (context: {
    session: InferAuthType<NonNullable<TAuth>>;
    ctx: ServiceContext;
    rbac?: TRBAC;
    params?: InferSchemaType<NonNullable<TParamsSchema>>;
    body?: InferSchemaType<NonNullable<TBodySchema>>;
    query?: InferSchemaType<NonNullable<TQuerySchema>>;
