{"output":"import { NextResponse } from "next/server";
import { Result } from "@repo/result";
import { createSuccessResponse } from "./createSuccessResponse";
import { createErrorResponse } from "./createErrorResponse";

interface HandleResultOptions<T> {
  result: Result<T, Error>;
  context: any;
  transformResponse?: (data: T, context: any) => unknown;
  successStatus?: number;
}

export function handleResult<T>({
  result,
  context,
  transformResponse,
  successStatus = 200,
}: HandleResultOptions<T>): NextResponse {
  if (!result.ok) {
    return createErrorResponse({
      message: result.error.message,
      error: result.error,
      context: typeof context === 'string' ? context : 'handleResult',
      status: 500
    });
  }

  if (result.data instanceof NextResponse) {
    return result.data;
  }

  const responseData = transformResponse
    ? transformResponse(result.data, context)
    : result.data;

  return createSuccessResponse(responseData, successStatus);
}
