{"output":"/**
 * Shared Types for Manual Join Operations
 *
 * WHY MANUAL JOINS?
 * ==================
 * Supabase's PostgREST API doesn't support SQL joins between tables that lack
 * foreign key relationships. Our database schema uses string IDs (member_id,
 * assignee_id, etc.) without actual foreign key constraints, so we must perform
 * joins manually in application code.
 *
 * PERFORMANCE CONSIDERATIONS:
 * ===========================
 * - Always use Map-based lookups for O(1) access time
 * - Batch fetch related data using IN queries to minimize DB round trips
 * - Cache lookup maps when performing multiple joins on the same data
 */

import type { ServiceParams } from "@repo/services";
import type { Json } from "@/types/database.types";

// ==================== BASE TYPES ====================

/**
 * Basic member details from the members table
 */
export interface MemberDetail {
  id: string;
  name: string;
  email: string;
  avatar_url: string | null;
}

/**
 * Team member relationship with member details joined
 */
export interface TeamMemberWithDetails extends MemberDetail {
  team_member_id: string;
  member_type: 'contributor' | 'viewer';
  role: string;
  is_active: boolean;
}

/**
 * Issue with assignee details joined
 */
export interface IssueWithAssignee {
  // All original issue fields from database
  assignee_id: string | null;
  completed_at: string | null;
  created_at: string | null;
  custom_metric_1: number | null;
  custom_metric_2: number | null;
  custom_metric_3: number | null;
  custom_metric_4: number | null;
  custom_metric_5: number | null;
  cycle_id: string | null;
  duration: number | null;
  estimate: number | null;
  id: string;
  imported_at: string | null;
  labels: string[] | null;
  organization_id: string;
  priority: string | null;
  source_data: Json;
  source_id: string;
  source_type: string;
  started_at: string | null;
  status: string | null;
  status_durations: Json | null;
  team_id: string | null;
  title: string;
  updated_at: string | null;

  // Joined fields
  assignee?: MemberDetail | null;
  assignee_member_type?: 'contributor' | 'viewer';
}

// ==================== GENERIC JOIN TYPES ====================

/**
 * Generic collection with joined member data
 */
export interface CollectionWithMembers<T> {
  items: (T & { member?: MemberDetail | null })[];
  memberLookup: Map<string, MemberDetail>;
}

/**
 * Generic collection with joined team member data
 */
export interface CollectionWithTeamMembers<T> {
  items: (T & { teamMember?: TeamMemberWithDetails | null })[];
  teamMemberLookup: Map<string, TeamMemberWithDetails>;
}

/**
 * Result of a generic join operation
 */
export interface JoinedCollection<T, R> {
  items: (T & Record<string, R | null>)[];
  lookup: Map<string, R>;
}

// ==================== JOIN CONFIGURATION ====================

/**
 * Configuration for member join operations
 */
export interface MemberJoinConfig {
  memberKey: string; // Field name containing member ID (e.g., 'assignee_id')
  joinAs?: string;   // Property name for joined data (default: 'member')
  fields?: string[]; // Member fields to fetch (default: ['id', 'name', 'email', 'avatar_url'])
  defaultValue?: MemberDetail | null; // Default when no match found
}

/**
 * Configuration for team member join operations
 */
export interface TeamMemberJoinConfig {
  memberKey: string;
  teamId: string;
  joinAs?: string;
  includeInactive?: boolean;
  defaultValue?: TeamMemberWithDetails | null;
}

/**
 * Configuration for generic join operations
 */
export interface JoinConfig<R> {
  primaryKey: string;   // Field in primary collection containing foreign key
  foreignKey: string;   // Field in lookup collection containing key
  joinAs: string;       // Property name for joined data
  defaultValue?: R | null;
}

// ==================== SERVICE INTERFACES ====================

/**
 * Base interface for join services
 * Extends ServiceParams with join-specific functionality
 */
export interface JoinServiceParams<TInput = void> extends ServiceParams<TInput> {
  // Additional join-specific configuration can be added here
  options?: {
    includeInactive?: boolean;
    batchSize?: number;
  };
}

/**
 * Lookup map for efficient joins
 */
export type LookupMap<T> = Map<string, T>;

// ==================== UTILITY TYPES ====================

/**
 * Extract member IDs from a collection
 */
export type ExtractMemberIds<T> = (collection: T[], memberKey: string) => string[];

/**
 * Create lookup map from collection
 */
export type CreateLookupMap<T> = (collection: T[], keyField: string) => Map<string, T>;","metadata":{"exit_code":0,"duration_seconds":0.0}}

